  for (const key of keys1) {
    if (!(isErrorObject && ['ref', 'context'].includes(key))) {
      const val1 = object1[key];
      const val2 = object2[key];

      if (
        (isObject(val1) || Array.isArray(val1)) &&
        (isObject(val2) || Array.isArray(val2))
