import React, { useState } from "react";

import imgBack from "../../../src/images/im4.jpg";
import load1 from "../../../src/images/load2.gif";
import axios from "axios";
import { toast } from "react-toastify";
import './ContactMe.css'

const ContactMe = () => {

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [banner, setBanner] = useState("");
    const [bool, setBool] = useState(false);

    // handle inputs
    const handleName = (e) => {
      setName(e.target.value);
    };

    const handleEmail = (e) => {
      setEmail(e.target.value);
    };

    const handleMessage = (e) => {
      setMessage(e.target.value);
    };

    const formSubmit = async (e) => {
      e.preventDefault();


      try {
        let data = {
          name,
          email,
          message,
        };

        setBool(true);

        const res = await axios.post(`/contact`, data);

        if (name.length === 0 || email.length === 0 || message.length === 0) {
          setBanner(res.data.msg);
          toast.error(res.data.msg);
          setBool(false);
        } else if (res.status === 200) {
          setBanner(res.data.msg);
          toast.success(res.data.msg);
          setBool(false);

          setName("");
          setEmail("");
          setMessage("");
        }
      } catch (err) {
        console.log(err);
      }
    };


    return (
      <section id="contact">
      <div className="container">
        <h1>Need Help? Don't forget to Contact With Us</h1>
        <p className="subtitle">Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's
          standard dummy text ever since the 1500s</p>
        <div className="row">
          <div className="col-md-3 mb-4">
            <div className="item mb-4">
              <div className="row">
                <div className="col-4">
                  <div className="icon">
                    <i className="fas fa-phone" />
                  </div>
                </div>
                <div className="col-8">
                  <span>+1 00 00 0000</span>
                  <span>+1 00 00 0000</span>
                </div>
              </div>
            </div>
            <div className="item mb-4">
              <div className="row">
                <div className="col-4">
                  <div className="icon">
                    <i className="fas fa-envelope" />
                  </div>
                </div>
                <div className="col-8">
                  <span>support@email.com</span>
                  <span>info@email.com</span>
                </div>
              </div>
            </div>
            <div className="item">
              <div className="row">
                <div className="col-4">
                  <div className="icon">
                    <i className="fas fa-map-marker-alt" />
                  </div>
                </div>
                <div className="col-8">
                  <span>67/A Lake View</span>
                  <span>New York, USA</span>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-9">
            <form>
              <div className="row">
                <div className="col">
                  <input type="text" className="form-control" placeholder="Name" name="name" id="name" />
                </div>
                <div className="col">
                  <input type="email" className="form-control" placeholder="Email" name="email" id="email" />
                </div>
              </div>
              <input type="text" className="form-control" placeholder="Subject" name="subject" id="subject" />
              <textarea placeholder="Message" name="message" rows={7} className="form-control" defaultValue={""} />
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
          </div>
        </div>
      </div>
    </section>
    )
}

export default ContactMe;
